<?php
namespace eStore\ShopBundle\Repository;

use Doctrine\ORM\EntityRepository;
use eStore\ShopBundle\Entity\Product;
/**
 * ProductRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ProductRepository extends EntityRepository
{
    public function getPopularProducts()
    {
        $query = $this->_em
                      ->createQuery("SELECT p, c
                                     FROM eStore\ShopBundle\Entity\Product p
                                     JOIN p.categories c
                                     ORDER BY p.id DESC
                                   ");
        return $query;
    }
    
    public function getProductCategories($product)
    {
        $pid = $product->getId();
        
         $qb = $this->createQueryBuilder('c')
                    ->select('c')
                    ->from('eStore\StoreBundle\Entity\Categoty', 'c')
                    ->innerJoin('category_product.category_id WHERE category_product.product_id=privati.id');

        return $qb->getQuery()
                  ->getResult();
    }
}